#include "MicroBit.h"

// This represents the micro:bit in code
MicroBit uBit;

int main() {
    // Always initialise the micro:bit first
    uBit.init();

    // We can control the microbit display using a 5x5 image.
    // An array of unsigned 8 bit integers (uint8_t) will represent
    // each LED's brightness, ranging from 0 to 255
    const uint8_t blink[25] = {
        255, 255, 255, 255, 255,
        255, 255, 255, 255, 255,
        255, 255, 255, 255, 255,
        255, 255, 255, 255, 255,
        255, 255, 255, 255, 255,
    };

    // The time between each blink
    int blinkTime = 1000;

    // Use an infinite loop to blink forever
    while(true) {
        // Set the micro:bit display image to our blink image
        // This turns all the LEDs on
        uBit.display.image.printImage(5, 5, blink);

        // Pause the micro:bit for blinkTime milliseconds. 
        uBit.sleep(blinkTime);

        // Clear the micro:bit display, turning all LEDs off
        uBit.display.clear();

        // Pause the micro:bit again
        uBit.sleep(blinkTime);
    }
}

